var xml2js = require('xml2js');
var xpath = require('./xpath');

var parser = new xml2js.Parser();
var xml = '<test><tag-1>test1 value</tag-1><tag2>test2 value</tag2></test>';

parser.parseString(xml, function (error, result) {
    if (!error) {
        try {
            console.log(xpath.find(result, '/test/tag-1'));
            console.log(xpath.find(result, '/test/tag2'));
        } catch (err) {
            console.error(err);
        }
    } else {
        console.error(error);
    }
});
